      console.log("context")
      setResult(eval(expression))
      console.log("result", result)
  }

  return (
    <NumberContext.Provider
      value={{
        calculate,
        result
      }}
    >
      {props.children}
    </NumberContext.Provider>
  );
};

export default NumberProvider;
